import { Component, Input } from '@angular/core';
import { Area } from 'area';
import { AreaService } from '../area.service';

@Component({
  selector: 'app-area',
  templateUrl: './area.component.html',
  styleUrls: ['./area.component.css'],
})
export class AreaComponent {
  @Input() area: Area | undefined;
  toggleLike(id: number): void {
    this.areaService.toggleLike(id);
  }
  constructor(private areaService: AreaService) {}

  ngOnInit() {
    // console.log('HER', this.area?.name);
  }
}
<div class="flex mb-10">
  <div>
    <img [src]="area?.image" class="h-[280px] w-80 rounded-xl" />
    <div
      class="relative -top-[260px] left-[280px] cursor-pointer"
      (click)="toggleLike(area?.id)"
    >
      <ng-icon name="heroHeartSolid" color="gray" size="26"></ng-icon>
    </div>
    <div class="-mt-2">
      <div class="flex justify-between">
        <div class="font-semibold">{{ area?.name }}</div>
        <div class="flex items-center gap-1">
          <ng-icon name="heroStarSolid"></ng-icon>{{ area?.rating }}
        </div>
      </div>
      <div class="text-gray-600">{{ area?.distance }} Kilometers away</div>
      <div class="text-gray-600">{{ area?.date | date : "dd/MM" }}</div>
      <div class="font-semibold">
        {{ area?.price | currency : "INR" }} night
      </div>
    </div>
  </div>
</div>
