import { Component, Input } from '@angular/core';
import { Area } from 'area';

@Component({
  selector: 'app-area',
  templateUrl: './area.component.html',
  styleUrls: ['./area.component.css'],
})
export class AreaComponent {
  @Input() area: Area | undefined;
  constructor() {}

  ngOnInit() {
    // console.log('HER', this.area?.name);
  }
}
<div class="flex">
  <div>
    <img [src]="area?.image" class="h-[280px] w-80 rounded-xl" />
    <div>
      <div class="flex">
        <div>{{ area?.name }}</div>
        <div>{{ area?.rating }}</div>
      </div>
      <div>{{ area?.distance }} Kilometers away</div>
      <div>{{ area?.date | date : "dd/MM" }}</div>
      <div>{{ area?.price | currency : "INR" }}</div>
    </div>
  </div>
</div>
