import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { AreaService } from '../area.service';
import { Area } from 'area';

@Component({
  selector: 'app-rooms',
  templateUrl: './rooms.component.html',
  styleUrls: ['./rooms.component.css'],
})
export class RoomsComponent {
  area: Area | undefined;
  constructor(
    private route: ActivatedRoute,
    private areaService: AreaService
  ) // private location: Location
  {}
  ngOnInit(): void {
    this.getArea();
  }

  getArea(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.areaService.getArea(id).subscribe((area) => (this.area = area));
    console.log(this.area);
  }
}
<div class="pt-28 px-20">
  <div>
    <div class="text-2xl font-semibold">
      {{ area?.name }}
    </div>
    <div class="font-semibold flex justify-between items-center">
      <div class="underline">1 review . Vagamon, Kerala, India</div>
      <div class="flex">
        <div
          class="flex items-center justify-center gap-2 hover:bg-gray-100 p-2 rounded-xl cursor-pointer"
        >
          <ng-icon name="heroShare" size="20"></ng-icon>
          <div class="underline">Share</div>
        </div>
        <div
          class="flex items-center justify-center gap-1 hover:bg-gray-100 p-2 rounded-xl cursor-pointer"
        >
          <div class="mt-2"><ng-icon name="heroHeart" size="20"></ng-icon></div>
          <div class="underline">Save</div>
        </div>
      </div>
    </div>
  </div>
  <div class="h-80 w-full bg-red-100"></div>
  <div class="mt-10">
    <div class="flex">
      <div class="w-9/12 flex justify-between mr-20 border-b-2 pb-4">
        <div class="flex-col">
          <div class="text-2xl font-semibold">Hosted by {{ area?.host }}</div>
          <div>
            {{ area?.bedrooms }} bedrooms . {{ area?.bathrooms }} bathrooms
          </div>
        </div>
        <div>
          <img [src]="area?.hostimage" class="h-16 w-16 rounded-full border" />
        </div>
      </div>
      <div class="w-3/12 bg-blue-200">bg</div>
    </div>
  </div>
</div>
